import './App.css';
import React, { Component } from "react";
import axios from 'axios';

// Core elements provided by NOAA (-snowdepth)
var PRCP = 0; // Total precipitation (mm/10)
var SNOW = 0; // Total snowfall (mm)
var TMAX = null; // Highest temperature
var TMIN = null; // Lowests temperature

export default class App extends Component {

  constructor (props)
  {
    super(props)

    this.stationID = '';

    this.list = null; // Individual date data

    this.main = (
      <div className="App" key={"search"}>
        <input onChange={(e) => {this.stationID = e.target.value}}></input>
        <button onClick={() => this.searchStation()}>Search</button>
      </div>
    );
  }

  // searchStation will retrieve the station id from the API and then display that data to the user
  searchStation() {
    axios.get("http://localhost:9999/searchstation", {
      params: {
        stationID: this.stationID
      }
    }).then(async (res) => {
      // Display new data if the station is valid
      if (res.data !== "INVALID STATION ID")
      {
        await res.data.forEach((line) => {
          line = line.split(",");
          // Find all core values for the station
          if (line[2] == "PRCP")
          {
            PRCP += parseInt(line[3]);
          }
          else if (line[2] == "SNOW")
          {
            SNOW += parseInt(line[3]);
          }
          else if (line[2] == "TMAX")
          {
            if (TMAX == null)
            {
              TMAX = parseInt(line[3]);
            }
            else if (parseInt(line[3]) > TMAX)
            {
              TMAX = parseInt(line[3])
            }
          }
          else if (line[2] == "TMIN")
          {
            if (TMIN == null)
            {
              TMIN = parseInt(line[3]);
            }
            else if (parseInt(line[3]) < TMIN)
            {
              TMIN = parseInt(line[3])
            }
          }
        }); 
        // Change the body of the page
        this.main = (
          <div className="App" key={"received"}>
            <p>Highest Temperature: {TMAX}</p>
            <p>Lowest Temperature: {TMIN}</p>
            <p>Total precipitation: {PRCP}</p>
            <p>Total Snowfall: {SNOW}</p>
            <input type="date" defaultValue="2017-01-01"></input>
          </div>
        );
        this.setState({received: true})
      }
      else
      {
        window.alert("No station was found with this ID")
      }
    });
  }

  render() {
    return this.main;
  }
}
