import './App.css';
import React, { useState } from "react";
import axios from 'axios';

var stationID = '';
var date = '';

// Core elements provided by NOAA (less snowdepth)
var PRCP = 0; // Total precipitation (mm/10)
var SNOW = 0; // Total snowfall (mm)
var TMAX = null; // Highest temperature
var TMIN = null; // Lowests temperature

var button = null;

var body = (
  <div>
    <input onChange={(e) => {stationID = e.target.value}}></input>
    {button}
  </div>
);

// searchStation will retrieve the station id from the API and then display that data to the user
function searchStation(state, setState) {
  axios.get("http://localhost:9999/searchstation", {
    params: {
      stationID: stationID,
      date: date
    }
  }).then((res) => {
    // Display new data if the station is valid
    if (res.data !== "INVALID STATION ID")
    {
      res.data.forEach((line) => {
        line = line.split(",");
        // Find all core values for the station
        if (line[2] == "PRCP")
        {
          PRCP += parseInt(line[3]);
        }
        else if (line[2] == "SNOW")
        {
          SNOW += parseInt(line[3]);
        }
        else if (line[2] == "TMAX")
        {
          if (TMAX == null)
          {
            TMAX = parseInt(line[3]);
          }
          else if (parseInt(line[3]) > TMAX)
          {
            TMAX = parseInt(line[3])
          }
        }
        else if (line[2] == "TMIN")
        {
          if (TMIN == null)
          {
            TMIN = parseInt(line[3]);
          }
          else if (parseInt(line[3]) < TMIN)
          {
            TMIN = parseInt(line[3])
          }
        }
      });
      // Change the body of the page
      body = (
        <div>
          <p>Highest Temperature: {TMAX}</p>
          <p>Lowest Temperature: {TMIN}</p>
        </div>
      );
      setState({
        state: state + 1
      });
      console.log(res.data);
    }
    else
    {
      window.alert("No station was found with this ID")
    }
  });
}

function App() {

  if (button == null)
  {
    const [state, setState] = useState({state: 0});

    button = (
      <button onClick={() => searchStation(state, setState)}>Search</button>
    );

    body = (
      <div>
        <input onChange={(e) => {stationID = e.target.value}}></input>
        {button}
      </div>
    );

    setState({state: state + 1})
  }

  return (
    <div className="App">
      <input type="date" min="2017-01-01" max="2017-12-31" defaultValue="2017-01-01" onChange={(e) => {date = e.target.value.substring(0, 4) + e.target.value.substring(5, 7) + e.target.value.substring(8, 10)}}></input>
      {body}
    </div>
  );
}

export default App;
